public static void quickSort(int[] arr) {
    System.out.println("");
    System.out.println("å¿«é€Ÿæ’åºå‰çš„æ•°ç»„ä¸ºï¼š ");
    for (int i : arr) {
        System.out.print(i + " ");
    }
    quickSort(arr, 0, arr.length - 1);
    System.out.println("");
    System.out.println("å¿«é€Ÿæ’åºåçš„æ•°ç»„ä¸ºï¼š");
    for (int i : arr) {
        System.out.print(i + " ");
    }
